//go:build cgo && ((linux && amd64) || (darwin && amd64) || (darwin && arm64) || (windows && amd64)) && sphincs_haraka_128s

package tests

import (
	"encoding/hex"
	"testing"

	"github.com/stretchr/testify/require"
  sphincsplus "github.com/katzenpost/sphincsplus/ref"
)

func TestSignVerifyVectorSpecifics(t *testing.T) {

	privKey := new(sphincsplus.PrivateKey)
	privKeyBytes, err := hex.DecodeString("d16a29731b6d10dc357923958fd6119b5409452a429b7b7eef8800e40b962c05cfbc79acfce9efdd194998cb08b6ca163cf44da2fde6f9c20b9350b05fa12f6f")
	require.NoError(t, err)
	err = privKey.FromBytes(privKeyBytes)
	require.NoError(t, err)

	pubKey := new(sphincsplus.PublicKey)
	pubKeyBytes, err := hex.DecodeString("cfbc79acfce9efdd194998cb08b6ca163cf44da2fde6f9c20b9350b05fa12f6f")
	require.NoError(t, err)
	err = pubKey.FromBytes(pubKeyBytes)

	signature, err := hex.DecodeString("")
	require.NoError(t, err)

	t.Logf("SPHINCS+ scheme parameters: %s", sphincsplus.SchemeName())
	t.Logf("priv key %x", privKey.Bytes())
	t.Logf("pub key %x", pubKey.Bytes())
	t.Logf("sig %x", signature)
	message := []byte("i am a message")
	require.True(t, pubKey.Verify(signature, message))

}
